create database GadgedGalaxyDB;
use GadgedGalaxyDB;

drop table Transactions;
drop table Wishlist;
drop table Roles;
drop table Users;
drop table Cart;
drop table Product;
drop table Category;

create table Category(
	CategoryID int primary key identity(1,1),
	CategoryName varchar(30) not null unique
)

create table Product(
	ProductID int primary key identity(1,1),
	ProductName varchar(30) not null,
	ProductCategory int not null,
	ProductPrice decimal(8, 2) not null,
	ProductShortDescription varchar(1000) not null,
	ProductDescription varchar(5000) not null,
	ProductQuantity smallint not null,
	ProductImage varchar(30) not null,
	foreign key(ProductCategory) references Category(CategoryID)
)

create table Roles(
	RoleID int primary key identity(1,1),
	RoleName varchar(20) not null
)

create table [Users] (
	UserID int primary key identity(1,1),
	UserFirstName varchar(30) not null,
	UserLastName varchar(30) not null,
	UserEmail varchar(50) not null unique,
	UserPassword nvarchar(60) not null,
	UserRole int not null,
	foreign key(UserRole) references Roles(RoleID)
)

create table Transactions(
	TransactionID int primary key identity(1,1),
	UserID int null,
	ProductID int null,
	Email varchar(150) null,
	Stock int not null,
	TotalPrice decimal(10, 2) not null,
	DateTimeOfTransaction DATETIME2(7) not null,	
	foreign key(UserID) references [Users](UserID),
	foreign key(ProductID) references Product(ProductID)
)

create table Wishlist(
	WishlistItemID int primary key identity(1,1),
	UserID int not null,
	ProductID int not null,
	foreign key(UserID) references [Users](UserID),
	foreign key(ProductID) references Product(ProductID)
)

create table Cart(
	CartItemID int primary key identity(1,1),
	UserID int not null,
	ProductID int not null,
	Quantity int not null,
	TotalPrice decimal(10, 2) not null,
	foreign key(UserID) references [Users](UserID),
	foreign key(ProductID) references Product(ProductID)
)

