create table CookieWishlist(
	CookieWishlistID int primary key identity(1,1),
	CookieWishlistCookieValue varchar(50) not null,
	CookieWishlistProduct int not null,
	foreign key(CookieWishlistCookieValue) references CookieHolder(CookieValue),
	foreign key(CookieWishlistProduct) references Product(ProductID)
