create table CookieCart(
	CookieCartID int primary key identity(1,1),
	CookieCartCookieValue varchar(50) not null,
	CookieCartProduct int not null,
	Quantity int not null,
	TotalPrice decimal(10, 2) not null,
	foreign key(CookieCartCookieValue) references CookieHolder(CookieValue),
	foreign key(CookieCartProduct) references Product(ProductID)
